// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity ^0.8.9;

import "../interface/core/IDefiBetsManager.sol";
import "../interface/core/IDefiBets.sol";
import "../interface/core/IDefiBetsPayoutRatio.sol";

contract MockManager is IDefiBetsManager {
    function getLPTokenSupplies() external pure returns (uint256, uint256) {
        uint256 _totalSupply = 6000 * 10 ** 18;
        uint256 _lockedSupply = 0;

        return (_totalSupply, _lockedSupply);
    }

    function setNewPayoutFactor(uint256 _payoutFactor) external {
        // dummy
    }

    function initialize(
        address defiBets,
        uint256 startExpTime,
        uint256 maxLossPerDay,
        uint256 minBetDuration,
        uint256 maxBetDuration,
        uint256 slot,
        uint256 maxWinMultiplier
    ) external {
        IDefiBets(defiBets).initializeData(
            startExpTime,
            maxLossPerDay,
            minBetDuration,
            maxBetDuration,
            slot,
            maxWinMultiplier
        );
    }

    function setBetForAccount(
        address defiBets,
        address user,
        uint256 betSize,
        uint256 minPrice,
        uint256 maxPrice,
        uint256 expTime,
        uint256 winning
    ) external {
        IDefiBets(defiBets).setBetForAccount(user, betSize, minPrice, maxPrice, expTime, winning);
    }

    function performExpiration(address defiBets, uint256 expTime, uint256 expPrice) external {
        IDefiBets(defiBets).performExpiration(expTime, expPrice);
    }

    function claimForAccount(address defiBets, address _account, uint256 _tokenId) external {
        IDefiBets(defiBets).claimForAccount(_account, _tokenId);
    }

    function setBetParameter(
        address defiBets,
        uint256 _maxLossPerExpTime,
        uint256 _minBetDuration,
        uint256 _maxBetDuration,
        uint256 _slot,
        uint256 _maxWinMultiplier,
        uint256 _timeDelta,
        uint256 _dependentTimeStamp
    ) external {
        IDefiBets(defiBets).setBetParamater(
            _maxLossPerExpTime,
            _minBetDuration,
            _maxBetDuration,
            _slot,
            _maxWinMultiplier,
            _timeDelta,
            _dependentTimeStamp
        );
    }

    function updatePlayerProfit(
        address payoutRatio,
        uint256 _currentPayoutFactor,
        uint256 _amount,
        uint256 _exptime
    ) external {
        IDefiBetsPayoutRatio(payoutRatio).updatePlayerProfit(_currentPayoutFactor, _amount, _exptime);
    }

    function updateLPProfit(address payoutRatio, uint256 _amount, uint256 _expTime) external {
        IDefiBetsPayoutRatio(payoutRatio).updateLPProfit(_amount, _expTime);
    }
}

contracts/core/DefiBetsVault.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../interface/core/IDefiBetsVault.sol";

error DefiBetsVault__Forbidden();
error DefiBetsVault__NotEnoughFunds();

contract DefiBetsVault is IDefiBetsVault,Ownable{

    using SafeMath for uint256;

    /* ====== State Variables ====== */

    uint256 public totalFees;

    mapping(uint256 => uint256) public expTimeSupply;

    address public token;
    address public defiBetsManager;


    constructor(address _defibetsManager,address _token){
        defiBetsManager = _defibetsManager;
        token = _token;
    }

    /* ====== Mutation Functions ====== */

    function deposit(address _from,uint256 _amount,uint256 _expTime,uint256 _fees) external {

        _isManager();

        bool _success = IERC20(token).transferFrom(_from,address(this),_amount.add(_fees));

        require(_success);

        
        uint256 _totalFees = totalFees;
        
        uint256 _supply = expTimeSupply[_expTime];
        expTimeSupply[_expTime] = _supply.add(_amount);
        
        totalFees = _totalFees.add(_fees);

        emit Deposit(_expTime,_amount,expTimeSupply[_expTime],totalFees);
    }

    function withdraw(address _to,uint256 _amount,uint256 _expTime) external  {

        _isManager();

        uint256 _supply = expTimeSupply[_expTime];

        if(_supply < _amount){
            revert DefiBetsVault__NotEnoughFunds();
        }

        bool _success =IERC20(token).transfer(_to,_amount);

        require(_success);

        expTimeSupply[_expTime] = _supply.sub(_amount);


        emit Withdraw(_to,_amount,_expTime,expTimeSupply[_expTime]);
    }

    function depositFromLP(uint256 _expTime,uint256 _amount) external {
        _isManager();

        _updateSupply(_expTime,_amount);
    }


    function withdrawFees() external onlyOwner(){
        uint256 _totalFees = totalFees;

        bool _success = IERC20(token).transfer(owner(), _totalFees);

        if(_success){
            totalFees = 0;
        }

        emit FessWithdrawd(_totalFees);
    }
    


    /* ====== Internal Functions ====== */

    function _isManager() internal view {
        if(msg.sender != defiBetsManager){
            revert DefiBetsVault__Forbidden();
        }
    }

    function _updateSupply(uint256 _expTime,uint256 _amount) internal {
        uint256 _supply = expTimeSupply[_expTime];
        expTimeSupply[_expTime] = _supply.add(_amount);
    }

}

